select "Auteurs"."Auteur_nom" , "Auteurs"."Auteur_pnom",count(*) as Nombre_oeuvres_vendues from "Oeuvres" inner join "Ventes" on "Oeuvres"."Oeuvres_id" = "Ventes"."Oeuvres_id" inner join "Auteurs" on "Auteurs"."Auteur_id" = "Oeuvres"."Auteur_id" group by "Oeuvres"."Auteur_id", "Auteurs"."Auteur_nom", "Auteurs"."Auteur_pnom" HAVING count(*) < best_auteur() *0.9 AND count(*) > best_auteur() * 0.75;